con:config.script:
def expectedResponse = """
<soap:Envelope xmlns:soap="http://www.w3.org/2003/05/soap-envelope" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xmlns:xsd="http://www.w3.org/2001/XMLSchema">
   <soap:Body>
      <AddResponse xmlns="http://tempuri.org/">
         <AddResult>10</AddResult>
      </AddResponse>
   </soap:Body>
</soap:Envelope>
"""

// Get the request test step
def soapRequest = testRunner.testCase.getTestStepByName("SOAP Request - Add")

// Get the response property of the request
def responseProperty = soapRequest.getProperty("Response")
def actualResponse = responseProperty?.value?.trim()

log.info "Actual Response: $actualResponse"
log.info "Expected Response: $expectedResponse"

// Function to clean response by removing whitespace and newlines
def cleanResponse(response) {
    return response.replaceAll("\\s+", "")
}

// Compare cleaned actual response with cleaned expected response
assert cleanResponse(actualResponse) == cleanResponse(expectedResponse)
